import "./App.css";
import React, { useEffect, useState, useRef } from "react";
import Navbar from "./components/Navbar.js";
import Classes from "./components/Classes.js";
import Music from "./components/Music.js";
import Bookings from "./components/Bookings.js";
import Class from "./components/Class";
import { Routes, Route, useLocation } from "react-router-dom";
import SpotifyPlayer from "./components/SpotifyPlayer";
import { TransitionGroup, CSSTransition } from "react-transition-group";
import Dashboard from "./components/Dashboard";

function App() {
	const location = useLocation();
	const [locationState, setLocationState] = useState(location)

	const getPathDepth = (location) => {
		let pathArr = location.pathname.split("/");
		return pathArr.filter((el) => el !== "").length;
	};

	// const [currentDepth, setCurrentDepth] = useState(getPathDepth(location));

	const [direction, setDirection] = useState("right");

	const prevDepthRef = useRef();


	useEffect(() => {
		prevDepthRef.current = getPathDepth(locationState);
		// setLocationState(location);
		if (getPathDepth(locationState) - prevDepthRef.current >= 0) {
			setDirection("right");
			console.log("right");
		} else {
			setDirection("left");
			console.log("left");
		}
	}, [location, locationState]);


	return (
		<div>
			<TransitionGroup component={null}>
				<CSSTransition key={locationState.key} classNames={'fade'} timeout={1000}>
					<div onClick={() => setLocationState(location)}>
						<Routes location={locationState}>
							<Route path="/" element={<Navbar />} />
							<Route path="/classes" element={<Classes />} />
							<Route path="/music" element={<Music />} />
							<Route path="/bookings" element={<Bookings />} />
							<Route path="/classes/:id" element={<Class />} />
							<Route path="/playlists/:id" element={<SpotifyPlayer />} />
							<Route path="/dashboard" element={<Dashboard />} />

							{/* add below 404 page */}
							{/* <Route path="*" element={<NoMatch />} /> */}
						</Routes>
					</div>
				</CSSTransition>
			</TransitionGroup>
		</div>
	);
}

export default App;
