				<Route exact path="/" element={<Navbar />} />
				<Route exact path="/classes" element={<Classes />} />
				<Route exact path="/music" element={<Music />} />
				<Route exact path="/bookings" element={<Bookings />} />
				<Route exact path="/classes/:id" element={<Class />}/>
