import React, { useState } from "react";

function Classes() {
	const [showCheckout, setShowCheckout] = useState(false);
	const [showClasses, setShowClasses] = useState(true);

	return (
		<div className="classes">
			{showClasses && (
				<div>
					<header>
						Classes are $15 each. After submitting payment you will receive an
						email with your class zoom link!
					</header>
					<ul className="class-list">
						<li onClick={goToCheckout} className="class-item">
							Saturday, 1/22/2022 @ 10:00am
						</li>
						<li className="class-item">Saturday, 1/29/2022 @ 10:00am</li>
						<li className="class-item">Saturday, 2/22/2022 @ 10:00am</li>
					</ul>
				</div>
			)}
		</div>
	);
}

export default Classes;
