import "./App.css";
import React, { useEffect, useState } from "react";
import Navbar from "./components/Navbar.js";
import Classes from "./components/Classes.js";
import Music from "./components/Music.js";
import Bookings from "./components/Bookings.js";
import Class from "./components/Class";
import { Routes, Route, Link, useLocation } from "react-router-dom";
import SpotifyPlayer from "./components/SpotifyPlayer";
import { TransitionGroup, CSSTransition } from "react-transition-group";
import Dashboard from "./components/Dashboard";

function App() {
	const location = useLocation();
	const [date, setDate] = useState("");
	const [time, setTime] = useState("");

	// const [slideObj, setSlideObj] = useState({ curr: 0, next: 0 });

	const getPathDepth = (location) => {
		let pathArr = location.pathname.split("/");
		return pathArr.filter((el) => el !== "").length;
	};

	// useEffect(() => {
	// 	setPathObj({currentPath: getPathDepth(location), nextPath: null})
	// });

	const [prevDepth, setPrevDepth] = useState("");

	// useEffect(() => {
	// 	setSlideObj({ curr: curr, next: getPathDepth(location) });
	// });

	// const handleSubmit = (e) => {
	// 	e.preventDefault();
	// 	createClass(date, time);
	// 	// reset form
	// 	setDate("");
	// 	setTime("");
	// };
	console.log(getPathDepth(location) - prevDepth >= 0 ? "right" : "left");

	console.log(`Prev depth: ${prevDepth}, newDepth: ${getPathDepth(location)}`);

	return (
		<div>
			{/* <div className="app-container"> */}
			{/* <form onSubmit={handleSubmit}>
					<input
						type="date"
						value={date}
						onChange={(e) => setDate(e.target.value)}
					/>
					<input
						type="time"
						value={time}
						onChange={(e) => setTime(e.target.value)}
					/>
					<input type="submit" value="Create Class" />
				</form> */}
			{/* </div> */}

			<TransitionGroup component={null}>
				<CSSTransition key={location.key} classNames="fade" timeout={500}>
					<div
						className={
							getPathDepth(location) - prevDepth >= 0 ? "right" : "left"
						}
						onClick={setPrevDepth(getPathDepth(location))}
					>
						<Routes location={location}>
							<Route exact path="/" element={<Navbar />} />
							<Route exact path="/classes" element={<Classes />} />
							<Route exact path="/music" element={<Music />} />
							<Route exact path="/bookings" element={<Bookings />} />
							<Route exact path="/classes/:id" element={<Class />} />
							<Route exact path="/playlists/:id" element={<SpotifyPlayer />} />
							<Route exact path="/dashboard" element={<Dashboard />} />

							{/* add below 404 page */}
							{/* <Route path="*" element={<NoMatch />} /> */}
						</Routes>
					</div>
				</CSSTransition>
			</TransitionGroup>
		</div>
	);
}

export default App;
