import React, { useEffect, useState } from "react";

function App() {
	const [date, setDate] = useState("");
	const [time, setTime] = useState("");

	const handleSubmit = (evt) => {
		evt.preventDefault();
		console.log(`Submitting date: ${date} and time: ${time}`);
	};

	// Create a new yoga class
	const createClass = () => {
		// Eventually bring in this data from date/time picker form
		const data = {
			topic: "test class with hidden env variables",
			type: 2,
			start_time: "2022-01-19T19:00:00",
			duration: 60,
		};

		// Send post request to express server with data from form
		fetch("/api", {
			method: "POST",
			headers: {
				"Content-Type": "application/json",
			},
			body: JSON.stringify(data),
		})
			.then((response) => response.json())
			.then((data) => {
				// Get back meeting details to add to state/re-render UI
				console.log(data);
			});
	};

	return (
		<div>
			<form onSubmit={handleSubmit}>
				<input
					type="date"
					value={date}
					onChange={(e) => setDate(e.target.value)}
				/>
				<input
					type="time"
					value={time}
					onChange={(e) => setTime(e.target.value)}
				/>
				<input type="submit" value="Create Class" />
			</form>
			<button onClick={createClass}>fetch data</button>
		</div>
	);
}

export default App;
